const dispatch = useDispatch()
const addItemHandler = (item) => {
    dispatch({
        type: "ADD_ITEM",
        payload: {
            item: item
        }
    })
}
const removeItemHandler = (id) => {
    dispatch({
        type: "REMOVE_ITEM",
        payload: {
            id: id
        }
    })
}
const clearItemHandler = () => {
    dispatch({
        type: "CLEAR_CART",
    })
}
