import axios from "axios";
import { useEffect } from "react";
import { useState } from "react";
import { NavLink, useLocation } from "react-router-dom";
const AuthIndex = () =>{
    const [params, setParams] = useState('');
    const location = useLocation()
    useEffect(()=>{
        setParams(location.pathname.replace('/',''))
    },[location])

    const [details, setDetails] = useState({
        email:'',
        password:''
    })

    const handleInput = (e) =>{
        setDetails({
            ...details,
            [e.target.name]:e.target.value
        })
    }
    const handleSubmission = (e) =>{
        e.preventDefault();
        if(params==="signup"){
            signupWithEmailAndPassword()
        }
    }

    const signupWithEmailAndPassword = async ()=>{
        try{
            await axios.post(`https://identitytoolkit.googleapis.com/v1/accounts:signUp?key=[AIzaSyCyUo859e-kKU0NkpuXKa8zuIHxsWR67Vc]`,
        {
            email:details.email, 
            password:details.password, 
            returnSecureToken:true
        })
        }catch(error){
            console.log(error)
        }


    }

    return(
        <div className="auth-container">
        <div className="auth-container--box">
            <div className="tab-selector">
                <NavLink to={"/login"}><h3>Login</h3></NavLink>
                <NavLink to={"/signup"}><h3>Signup</h3></NavLink>
            </div>
            <form autoComplete={"off"} onSubmit={handleSubmission}>
                <div className="input-wrap">
                    <label htmlFor="email">Email</label>
                    <input 
                        type="text" 
                        name="email" 
                        placeholder="Enter Email" 
                        value={details.email}
                        onChange={handleInput}
                    />
                </div>
                <div className="input-wrap">
                    <label htmlFor="password">Password</label>
                    <input 
                        type="password" 
                        name="password" 
                        placeholder="Enter Password" 
                        value={details.password}
                        onChange={handleInput}
                    />
                </div>
                <div className="button-wrap">
                    <button className="login-btn">
                        {params === "login" ? "Login" : "Signup"}
                    </button>
                </div>
            </form>
        </div>
    </div>
    )
}
export default AuthIndex;
