    const dispatch = useDispatch()

    const descreaseCounterByOne = (event) => {
        dispatch({
            type: "REMOVE_ITEM",
            payload: {
                id: data.id
            }

        })
    }
    const increaseCounterByOne = (event) => {
        dispatch({
            type: "ADD_ITEM",
            payload: {
                item: data
            }
        })
    }

    return (
        <div className="checkout-modal_list-item">
            <div className="img-wrap">
                <img src={`/assets/${item.thumbnail}`} className="img-fluid" alt="Placeholder" />
            </div>
            <div className="information">
                <div>
                    <h4>{item.title}</h4>
                    <div className="pricing">
                        <span>{item.price}</span>
                        <small>
                            <strike>{item.discountedPrice}</strike>
                        </small>
                    </div>
                </div>
                <div className="cart-addon cart-addon__modal">
                    <button onClick={() => onEmitDecreaseCounter(item.id)}>-</button>
                    <span className="counter">{item.quantity}</span>
                    <button onClick={() => onEmitIncreaseCounter(item.id)}>+</button>
                </div>
            </div>
        </div>
    )
}
export default CartItem;
