import AddtoCartIcon from '../../../assets/icons/add_cart.svg';
import { useState } from 'react';
const ListItem = ({ data }) => {

    const [message, setMessage]=useState("Not added to the cart yet")

    const handleClick = () => {
        console.log('Click 11')
        setMessage("Added to the cart")
    }

    return (
        <div className={"item-card"}>
            <img className={"img-fluid"} src={`/assets/${data.thumbnail}`} alt="placeholder" />
            <div className={"item-card__information"}>
                <div className={"pricing"}>
                    <span>{data.discountedPrice}</span>
                    <small><strike>{data.price}</strike></small>
                </div>
                <div className={"title"}><h3>{data.title}</h3></div>
            </div>
            <small className={"cart-message"}>{message}</small>
            <button className={"cart-add"} onClick={handleClick}>
                <span>Add to cart</span>
                <img src={AddtoCartIcon} alt="Cart Icon" />
            </button>
        </div>

    )
}
export default ListItem;
