import { useEffect, useState } from "react";
import ListItem from "./ListItems/ListItem";
import axios from "axios";
import Loader from "../UI/Loader";
const Products = ({ onAddItem, onRemoveItem, eventList }) => {

    const [items, setItems] = useState([]);
    const [loader, setLoader] = useState(true);
    // const [presentItems, setPresentItems] = useState([]);

    useEffect(() => {
        async function fetchItems() {
            try {
                const response = await axios.get(`https://shopping-app-b9c1a-default-rtdb.firebaseio.com/items.json`)
                const data = response.data.map((item, index) => {
                    return {
                        ...item,
                        quantity: 0,
                        id: index
                    }
                })
                setItems(data)
                setLoader(false)
            } catch (error) {
                console.log(error)
                setLoader(false)
            } finally {
                setLoader(false)
            }

        }
        fetchItems();

    }, [])

    useEffect(() => {
        if (eventList.id) {
            if (eventList.type === 1) {
                handleAddItem(id)
            } else if (eventList.type === -1) {
                handleRemoveItem(id)
            }
        }
    }, [eventList])

    const handleAddItem = (id) => {
        let data = [...items]
        let index = data.findIndex(i => i.id === id)
        data[index].quantity += 1;
        setItems([...data])
        onAddItem(data[index])


    }
    const handleRemoveItem = (id) => {
        let data = [...items]
        let index = data.findIndex(i => i.id === id)
        if (data[index].quantity != 0) {
            data[index].quantity -= 1;
            setItems([...data])
            onRemoveItem(data[index])
        }



    }
    return (
        <>
            <div className={"product-list"}>
                <div className={"product-list--wrapper"}>
                    {items.map((item) => {
                        return <ListItem key={item.id} data={item} onAdd={handleAddItem} onRemove={handleRemoveItem} />
                    })}

                </div>
            </div>
            {loader && <Loader />}
        </>
    )

}
export default Products;
