import axios from "axios"

export const addItemHandler = (item) => {
    return dispatch => {
        dispatch({
            type: "ADD_ITEM",
            payload: {
                item: item
            }
        })
    }

}
export const removeItemHandler = (id) => {
    return dispatch => {
        dispatch({
            type: "REMOVE_ITEM",
            payload: {
                id: id
            }
        })
    }
}
export const clearItemHandler = () => {
    return dispatch => {
        dispatch({
            type: "CLEAR_CART",
        })
    }
}

export const placeOrderHandler = () => {
    return async (dispatch, getState) => {
        try{
            const{auth, cart } = getState()
            await axios.post(`https://shopping-app-b9c1a-default-rtdb.firebaseio.com/orders/${auth.localId}.json?auth=${auth.idToken}`,{
                ...cart
            })
        }catch(error){

        }

    }
}
